<template>
  <div class="labelManagement">
    <div class="labelMain flex">
          <div style="position: relative;margin-left: 300px">
            <img :src="imgBase" >
            <div class="absoulate">
              【{{signa}}】{{content}},退订回TD
            </div>
          </div>
          <div class="floatRight">
            <el-form size="small">
              <el-form-item label="群发对象：" class="flexCenter" >
                <el-radio-group v-model="radioValue">
                  <el-radio label="0">
                    输入手机号
                  </el-radio>
                  <el-radio label="1">
                    按人群筛选
                  </el-radio>
                </el-radio-group>
              </el-form-item>
              <el-form-item  class="flexCenter" style="padding-left: 84px" v-if="radioValue==0">
                <el-input
                  type="textarea"
                  autosize
                  style="min-height: 85px;border: 0px;width: 200px"
                  placeholder="仅支持大陆手机号，多个手机号请换行"
                  resize="none"
                  v-model="mobiles"></el-input>
              </el-form-item>
              <el-form-item class="flexCenter" style="padding-left: 84px" v-if="radioValue==1">
                <el-select  placeholder="请选择"  v-model="crowdId" style="width: 200px">
                  <template v-for="(shop,shopIn) in crowdList">
                    <el-option :label="shop.name" :value="shop.id"></el-option>
                  </template>
                </el-select>
<!--                <div class="error">当前可送达人数为0，请重新选择人群</div>-->
              </el-form-item>

              <el-form-item label="短信内容：" class="flex">
                <div class="messageBorder">
                      <div class="messageHeader">
                              <span class="current" @click="selectModel">选择模板</span> | <span @click="modelNew" class="current">存为新模板</span>
                      </div>
                      <div class="messageInput">
                        <el-input
                          type="textarea"
                          autosize
                          class="textarea"
                          style="min-height: 85px;"
                          placeholder="此处为短信模板内容"
                          resize="none"
                          v-model="content">
                        </el-input>
                      </div>
                </div>
              </el-form-item>

              <el-form-item label="短信签名：" class="flex">
                <span>【{{signa}}】</span><span class="colorF current" @click="saveShow=true" v-if="!saveShow">修改</span>
                <template v-if="saveShow">
                  <el-input v-model="signaText" style="display: inline-block;width: 100px"></el-input>
                  <span class="colorF current" style="margin-left: 10px" @click="saveValue">保存</span>
                  <span class="colorF current" style="margin-left: 10px" @click="saveShow=false">取消</span>
                </template>
              </el-form-item>

              <el-form-item label="发送时间：" class="flex">
                <el-radio-group  v-model="times">
                  <el-radio label="0" class="radioTop">
                    立即发送
                  </el-radio>
                  <el-radio label="1" class="radioBottom">
                    定时发送
                    <el-date-picker
                      v-model="send_time"
                      class="dateTime"
                      type="datetime"
                      value-format="yyyy-MM-dd HH:mm:ss"
                      placeholder="选择日期时间">
                    </el-date-picker>
                  </el-radio>
                </el-radio-group>
              </el-form-item>


            </el-form>
          </div>
    </div>
    <div class="all">
      <el-button class="submits" @click="cancelPro" >取消</el-button>
      <el-button :loading="loading" style="margin-left: 100px"  type="primary" @click="saveData">保存</el-button>
    </div>



    <el-dialog
      title="选择模板"
      width="50%"
      @close="modelCancel"
      append-to-body
      :visible.sync="isModel"
      class="setting"
    >
      <el-table
        ref="singleTable"
        :data="templates"
        highlight-current-row
        @current-change="handleCurrentChange"
        style="width: 100%">
        <el-table-column
          type="index"
          width="50">
        </el-table-column>
        <el-table-column
          property="content"
          label="内容"
          >
        </el-table-column>

        <el-table-column
          label="删除"
          align="center"
          width="100">
          <template slot-scope="scope">
            <el-button @click="handleClick(scope.$index)" type="text" size="small">删除</el-button>
          </template>
        </el-table-column>
      </el-table>
      <el-pagination
        style="margin-top: 20px;text-align: center;"
        @current-change="currentChange"
        background
        :page-size="pageSize"
        layout="prev, pager, next"
        :total="totalCount">
      </el-pagination>
    </el-dialog>
  </div>
</template>

<script lang="ts">
    import {
        Component,
        Vue,
        Watch
    } from 'vue-property-decorator';
    import {
        Action,
        State
    } from 'vuex-class'

    import  {smsGetTemplates,smsDelTemplate,smsAddTemplate,getCrowds,addBatch} from '@/common/fetch'
    import fa from "element-ui/src/locale/lang/fa";

    @Component({
        mixins:[],
        components: {

        }
    })

    export default class AddProduct extends Vue {
        imgBase='data:image/png;base64,'
        content=''
        page=1
        pageSize=10
        totalCount=0
        isModel=false
        crowdList=[]
        crowdId=''
        radioValue='0'
        mobiles='' //手机号
        signa=''
        signaText=''
        saveShow=false
        send_time=''
        times="0"
        loading=false
        cancelPro(){
            this.$router.push({
                name:'CrowdClient'
            })
        }
        saveValue(){
            this.signa=this.signaText
            this.saveShow=false
        }
        modelCancel(){
            this.isModel=false
        }
        selectModel(){
            this.modelQuery()
            this.isModel=true
        }

        modelQuery(){
            let data={
                page:this.page,
                pageSize:this.pageSize
            }
            smsGetTemplates().then(res=>{
                if(res.errorCode==0){
                    this.templates=res.data
                    this.totalCount=res.totalCount
                }
            })
        }
        handleCurrentChange(val){
            this.isModel=false
            this.content=val.content
        }
        //删除模板
        handleClick(index){
            let id=this.templates[0].id
            this.$confirm('此操作将删除该模板, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
                smsDelTemplate({id:id}).then(res=>{
                    if(res.errorCode==0){
                        this.$message({
                            type: 'success',
                            message: res.msg
                        });
                    }
                    this.isModel=false
                    this.modelQuery()
                })

            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: '已取消删除'
                });
            });
        }
        //存为新模板
        modelNew(){
            smsAddTemplate({content:this.content}).then(res=>{
                if(res.errorCode==0){
                    this.$message({
                        type: 'success',
                        message: res.msg
                    });
                }
            })
        }

        currentChange(val){
            this.page=val;
            this.modelQuery();
        }

        //保存
        saveData(){
            if(this.loading)return
            this.loading=true
            let data={
                signa:this.signa,
                type:1,
                content:this.content
            }
            if(this.radioValue==0){
                data.mobiles=this.mobiles
            }else if(this.radioValue==1){
                data.crowd_id=this.crowdId
            }
            if(this.times==0){
                data.send_time=0
            }else if(this.times==1){
                data.send_time=this.send_time
            }
            addBatch(data).then(res=>{
                if(res.errorCode==0){
                    this.$message({
                        type: 'success',
                        message: res.msg
                    });
                    let that=this
                    setTimeout(function () {
                        that.$router.push({
                            name:'CrowdClient'
                        })
                    },1000)
                }
            }).catch(e=>{
                this.loading=false
            })

        }

        templates=[]
        async created(){
            let id = this.$route.params.id
            //获取人群列表
            getCrowds({page:1,pageSize:10000}).then(res=>{
                  if(res.errorCode==0){
                      this.crowdList=res.data
                      if(id){
                          this.radioValue="1"
                          this.crowdId=id
                      }
                  }
            })
        }




    }

</script>

